import React, { useState } from "react";
import "./search.css";
// Date Range library
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css"; // main css file
import "react-date-range/dist/theme/default.css"; // theme css file
// date-fns library for formating date
import { format } from "date-fns";

function Search() {
  const [openDateBox, setOpenDateBox] = useState(false);
  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);
  return (
    <div className="search">
      <div className="searchbox">
        <p className="boxTitle">Search</p>
        <label className="inputLabel" htmlFor="searchBar">Destination:</label>
        <input type="text" className="searchBar" id="searchBar" placeholder="Type your destination..." />
        <span className="inputLabel">Check-in-date:</span>
        <span className="dateBar" onClick={() => setOpenDateBox(!openDateBox)}>{`${format(date[0].startDate, "MM/dd/yyyy")} to ${format(date[0].endDate, "MM/dd/yyyy")}`}</span>
        {openDateBox && <DateRange className="dateBoxList" editableDateInputs={true} onChange={(item) => setDate([item.selection])} moveRangeOnFirstSelection={false} ranges={date} />}
      </div>
      <div className="optionsBoxList">
        <p className="boxTitle">Options</p>
        <div className="optionBoxItem">
          </div><label className="inputLabel" htmlFor="min">Min price <span>{"(per night)"}:</span></label><input type="number" name="min" id="min" />
        <div className="optionBoxItem">
          </div><label className="inputLabel" htmlFor="min">Max price <span>{"(per night)"}:</span></label><input type="number" name="max" id="max" />
        <div className="optionBoxItem">
          </div><label className="inputLabel" htmlFor="min">Adult:</label><input type="number" name="adult" id="adult" />
        <div className="optionBoxItem">
          </div><label className="inputLabel" htmlFor="min">Children:</label><input type="number" name="children" id="children" />
        <div className="optionBoxItem">
          </div><label className="inputLabel" htmlFor="min">Room:</label><input type="number" name="room" id="room" />
      </div>
      <button className="serachButton">Search</button>
    </div>
  );
}

export default Search;
