import React, { useEffect, useState } from "react";
import axios from "axios";

function HookFetchDataAll() {
  const [posts, setPosts] = useState({});
  useEffect(() => {
    axios
      .get(`https://jsonplaceholder.typicode.com/posts`)
      .then((res) => {
        console.log(res);
        setPosts(res.data);
        console.log('posts');
        console.log(posts);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
console.log(posts);
  return (
    <div>Hello {posts}</div>
  );
}

export default HookFetchDataAll;
