import React from "react";
import useInputCustomHook from "./useInputCustomHook";

function InputCustom() {
  const [fname, bindFirstname , resetFname] = useInputCustomHook();
  const [lname, bindLasttname , ] = useInputCustomHook();
  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Hello ${fname} ${lname}`)
    resetFname()
    resetLname()
  };
  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>First Name</label>
        <input type={"text"} {...bindFirstname}></input>
      </div>
      <div>
        <label>Last Name</label>
        <input type={"text"} {...bindLasttname}></input>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
}

export default InputCustom;
