import React , {useReducer} from 'react'

const initialstate = {
  firstCounter : 0,
  secondCounter : 10
}

const reducer = (state , action) => {
  switch(action.type) {
    case "increment" : return {...state ,firstCounter : state.firstCounter + action.val};
    case "decremnt" : return {...state ,firstCounter : state.firstCounter - action.val};
    case "incrementTwo" : return {...state ,secondCounter:  state.secondCounter + action.val};
    case "decremntTwo" : return {...state ,secondCounter:  state.secondCounter - action.val};
    case "reset" : return initialstate;
    default : return state 
  }
}

function Test() {
  const [state , dispatch] = useReducer(reducer,initialstate)
  return (
  <div>
    He
    <div>cont one | {state.firstCounter}</div>
    <button onChange={dispatch({type : "increment" , val : 1})}>Increment One</button>
    <button onChange={dispatch({type : "decremnt" , val : 1})}>Decrement One</button>
    <div>cont two | {state.secondCounter}</div>
    <button onChange={dispatch({type : "incrementTwo" , val : 5})}>Increment One</button>
    <button onChange={dispatch({type : "decremntTwo" , val : 5})}>Decrement One</button>
    <button onChange={dispatch("decremntTwo")}>Reset</button>
  </div>
  )
}

export default Test
