import React, { useState, useEffect } from "react";

function HookMouse() {
  const [x, setX] = useState(0);
  const [y, setY] = useState(0);
  const printMouseMove = (e) => {
    console.log('Mouse Move');
    setX(e.clientX);
    setY(e.clientY);
  };
    // The Second Parameter Is For What The Effect depences In Changes After Rendering And If He Empety That means NO THING Just
    // Set Effect Once
    // *****
    // The return Statment Is Execute When The Component Is Removed  And It's Accept Function
    // So You Can Do What U Need When The Component Is Removed
  useEffect(() => {
    console.log("useEffect Called");
    window.addEventListener("mousemove", printMouseMove);
    return () => {
      console.log("The Component Removed");
      window.addEventListener("mousemove", printMouseMove);
    }
  } , []);
  return (
    <div>
      X - {x} AND Y - {y}
    </div>
  );
}

export default HookMouse;
