import React, { useState , useMemo } from "react";

function UseMemoHook() {
  const [counterOne, setCounterOne] = useState(0);
  const [counterTwo, setCounterTwo] = useState(0);
  const incrementOne = () => {
    setCounterOne(counterOne + 1);
  };
  const incrementTwo = () => {
    setCounterTwo(counterTwo + 1);
  };
  // Caching The Result But When counterOne is Changed Recalulate The isEven Function And Cache it
  const isEven = useMemo(() => {
    let i = 0; 
    while(i < 200000000)
    i++;
    if (counterOne % 2 === 0) return "Even";
    else return "Odd";
  } , [counterOne])
  return (
    <div>
      <button onClick={incrementOne}>Click {counterOne}</button>
      <span>{isEven}</span>
      <br></br>
      <button onClick={incrementTwo}>Click {counterTwo}</button>
    </div>
  );
}

export default UseMemoHook;
