import React from 'react'
import {NavLink} from 'react-router-dom'
import { useAuth } from "./auth";

function NavBar() {
  const ifTheLinkActive = ({isActive}) => {
    return {
      fontWeight : isActive? 'bold' : 'normal',
      color : isActive? 'red' : 'black',
      fontSize: isActive? '20px' : '16px' 
    }
  }
  const auth = useAuth()
  const ifUserLogged = auth
  return (
    <nav  className='primary'>
      <NavLink to='/' style={ifTheLinkActive}>Home</NavLink>
      <NavLink to='/about' style={ifTheLinkActive}>About</NavLink>
      <NavLink to='/product' style={ifTheLinkActive}>Product</NavLink>
      <NavLink to='/users' style={ifTheLinkActive}>Users</NavLink>
      <NavLink to='/profile' style={ifTheLinkActive}>Profile</NavLink>
    </nav>
  )
}

export default NavBar
