import React, { useReducer } from "react";

const initialState = {
  firstCounter : 0, 
  secondCounter : 10 }

const reducer = (state, action) => {
  switch (action.type) {
    case "increment":
      return {...state , firstCounter : state.firstCounter + action.value};
    case "decrement":
      return {...state , firstCounter : state.firstCounter - action.value};
    case "reset":
      return initialState;
    case "increment2":
      return {...state , secondCounter : state.secondCounter + action.value};
    case "decrement2":
      return {...state , secondCounter : state.secondCounter - action.value};
    default:
      return state;
  }
};

function UseReducerTwoCounter() {
  const [count, dispatch] = useReducer(reducer, initialState);
  return (
    <>
      <div>First Counter Is : {count.firstCounter} And Second Counter Is : {count.secondCounter}</div>
      <button onClick={() => dispatch({type : "increment" , value : 1})}>Increment</button>
      <button onClick={() => dispatch({type : "decrement", value : 1})}>Decrement</button>
      <button onClick={() => dispatch({type : "increment" , value : 5})}>Increment 5</button>
      <button onClick={() => dispatch({type : "decrement", value : 5})}>Decrement 5</button>
      <button onClick={() => dispatch({type : "reset"})}>Reset</button>
      <div>
      <button onClick={() => dispatch({type : "increment2" , value : 1})}>Increment Second Couner</button>
      <button onClick={() => dispatch({type : "decrement2", value : 1})}>Decrement Second Couner</button>
      </div>
    </>
  );
}

export default UseReducerTwoCounter;
