import React from "react";

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
			userName : ''
		};
  }

	handleChange = (event) => {
		this.setState({
			userName : event.target.value
		}, () => {console.log(this.state.userName);})
	}

	render()
	{
		return (
		<div>
			<input type='text' value={this.state.userName} onChange={this.handleChange}/>
		</div>
		)
	}
}

export default Form;
