import React, {  useState } from "react";
import Title from "./Title";
import AgeButton from "./AgeButton";
import SalaryButton from "./SalaryButton";

function ParentCompo() {
  const [age, setAge] = useState(22);
  const [salary, setSalary] = useState(5000);
  const incrementAge = () => {
    setAge(age + 1);
  };
  const incrementSalary = () => {
    setSalary(salary + 500);
  };
  return (
    <div>
      <Title />
      <AgeButton age={age} incrementAge={incrementAge}>increment Age</AgeButton>
      <SalaryButton salary={salary}  incrementSalary={incrementSalary}>increment Salary</SalaryButton>
    </div>
  );
}

export default ParentCompo;
