import React from 'react'
import {Outlet , useSearchParams} from 'react-router-dom'

function Users() {
  const [searchParams , setSearchParams] = useSearchParams();
  const activeUsers =  searchParams.get('filter') == 'active'
  return (
    <div>
      <h1>User 1</h1>
      <h1>User 2</h1>
      <h1>User 3</h1>
      <Outlet/>
      <div>
        <button onClick={() => setSearchParams({filter : 'active'})}>Show Active Users</button>
        <button onClick={() => setSearchParams({})}>Show All Users</button>
      </div>
      {
        activeUsers ? <h2>Active Users</h2> : <h2>All Users</h2>
      }
    </div>
  )
}

export default Users
