import React from 'react'

class Test extends React.Component {
  constructor()
  {
    super()
    this.state = {
      num: 0
    }
  }
  changeState() {
    this.setState(prevState => ({num : this.prevState}))
  }
  render(){
    return (
      <div>
        <h1>Count -{this.state.num}</h1>
        <button onClick={() => this.changeState()}>Click Me!</button>
      </div>
    )
  }

}

export default Test
