import React , {useState} from "react";
// Date Range library
import "react-date-range/dist/styles.css"; // main css file
import "react-date-range/dist/theme/default.css"; // theme css file
// date-fns library for formating date
import { format } from "date-fns";

function Search() {
  const [openDateBox, setOpenDateBox] = useState(false);
  return (
    <div className="search">
      <div className="searchbox">
        <label htmlFor="searchBar">Destination:</label>
        <input type="text" className="searchBar" id="searchBar" placeholder="Type your destination..." />
        <label htmlFor="dateBar">Check-in-date:</label>
        <span className="dateBar" onClick={() => HsetOpenDateBox()}>{`${format(date[0].startDate, "MM/dd/yyyy")} to ${format(
          date[0].endDate,
          "MM/dd/yyyy"
        )}`}</span>
        {openDateBox && <DateRange className="dateBox" editableDateInputs={true} onChange={(item) => setDate([item.selection])} moveRangeOnFirstSelection={false} ranges={date} />}
      </div>
    </div>
  );
}

export default Search;
