import React from "react";
// import CompoRefs from "./CompoRefs";
import FRInput from "./FRInput";

class Refs extends React.Component {
  constructor() {
    super();
		this.refInput = React.createRef();
  }
	// First Way
	clickHandler = () => {
		this.refInput.current.foucsIt()
	}
	// 
	// 
	// Second Way
	clickHandlerFR = () => {
		this.refInput.current.focus()
	}
	render() {
		return (
			<div>
				{/* <CompoRefs ref={this.refInput}/> */}
				<FRInput ref={this.refInput}/>
				<button onClick={this.clickHandlerFR}>Click</button>
			</div>
		)
	}
}
export default Refs;
