import React, { useReducer } from "react";
import ACompo from "./ACompo";
import BCompo from "./BCompo";
import CCompo from "./CCompo";

const initialCounter = 0;
const reducer = (state, action) => {
  switch (action) {
    case "increment":
      return state + 1;
    case "decrement":
      return state - 1;
    case "reset":
      return initialCounter;
    default:
      return state;
  }
};

export const ContextCounter = React.createContext();

function MainCompo() {
  const [count, dsipatch] = useReducer(reducer, initialCounter);
  return (
    <ContextCounter.Provider value={{ countValue: count, dsipatchCount: dsipatch }}>
      MainCompo
      <ACompo />
      <BCompo />
      <CCompo />
    </ContextCounter.Provider>
  );
}

export default MainCompo;
