import React, { useState } from "react";
import "./flightsoffers.css";
import img from "../../imgs/flights/img.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCalendar, faPlaneArrival, faPlaneDeparture } from "@fortawesome/free-solid-svg-icons";

function FlightsOffers() {
  const [flight, setFlight] = useState({ from: "", to: "" });
  return (
    <div className="FlightsOffers">
      <div class="planeImg">
        <img src={img} alt="plane" />
      </div>
      <div className="FlightsOffersText">
        <h1>Where Do You Go?</h1>
        <div className="flightBox">
          <FontAwesomeIcon icon={faPlaneDeparture} />
          <input type="text" className="flightInput" var={flight} onChange={(e) => setFlight({ ...flight, [e.target.name]: e.target.value })} name="from" id="from" />
        </div>
        <div className="flightBox">
          <FontAwesomeIcon icon={faPlaneArrival} />
          <input type="text" className="flightInput" var={flight} onChange={(e) => setFlight({ ...flight, [e.target.name]: e.target.value })} name="to" id="to" />
        </div>
        <div className="flightBox">
          <FontAwesomeIcon icon={faCalendar} />
          <span className="dateFlight"></span>
        </div>
      </div>
    </div>
  );
}

export default FlightsOffers;
