import React , {useReducer} from 'react'

const initialsta = {
  firstCounter : 0,
  secondCounter : 10
}

const reducer = (state , action) => {
  switch(action.type) {
    case "increment" : return state.firstCounter = state.firstCounter + action.val;
    case "decremnt" : return state.firstCounter = state.firstCounter - action.val;
    case "incrementTwo" : return state.secondCounter = state.secondCounter + action.val;
    case "decremntTwo" : return state.secondCounter = state.secondCounter - action.val;
    case "reset" : return initialsta;
    default : return state 
  }
}

function Test() {
  const [state , dispatch] = useReducer(reducer,initialsta)
  return (
  <div>
    <div>cont one | {state.firstCounter}</div>
    <button onChange={dispatch("increment")}>Increment One</button>
    <button onChange={dispatch("decremnt")}>Decrement One</button>
    <div>cont two | {state.secondCounter}</div>
    <button onChange={dispatch("incrementTwo")}>Increment One</button>
    <button onChange={dispatch("decremntTwo")}>Decrement One</button>
    <button onChange={dispatch("reset")}>Reset</button>
  </div>
  )
}

export default Test
