import React, { useEffect, useState } from "react";
import axios from "axios";

function HookFetchDataAll() {
  const [posts, setPosts] = useState({});
  useEffect(() => {
    axios
      .get(`https://jsonplaceholder.typicode.com/posts`)
      .then((res) => {
        console.log(res);
        setPosts(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  return (
    <div>
      {posts.map((post) => {
        return (
          <div>
            <strong>{post.title}</strong>
            <p>{post.body}</p>
            <br />
          </div>
        );
      })}
    </div>
  );
}

export default HookFetchDataAll;
