import React from "react";
import CompoHookB from "./CompoHookB";

export const UserFname = React.createContext();
export const UserLname = React.createContext();

//  EX: For Return Two Values Of Context

function CompoHookA() {
  return (
    <UserFname.Provider value={'Ammar'}>
      <UserLname.Provider value={'Al-Ahdab'}>
        <CompoHookB />
      </UserLname.Provider>
    </UserFname.Provider>
  );
}

export default CompoHookA;
