import React from 'react'
import {NavLink} from 'react-router-dom'
function NavBar() {
  const ifTheLinkActive = (isActive) => {
    return {
      fontWight : isActive? 'Bold' : 'N'
    }
  }
  return (
    <nav>
      <NavLink to='/' style={ifTheLinkActive}>Home</NavLink>
      <NavLink to='/about' style={ifTheLinkActive}>About</NavLink>
    </nav>
  )
}

export default NavBar
