import React, { useEffect, useReducer } from "react";
import axios from "axios";
const initialState = {
  loading: true,
  post: {},
  error: "",
};

const reducer = (state, aciton) => {
  switch (aciton.type) {
    case "SUCCESS_REQUEST":
      return { loading: false, post: aciton.pyload, error: "" };
    case "ERORR_REQUEST":
      return { loading: false, post: {}, error: "Something Went Error" };
    default:
      return state;
  }
};

function FetchDataUseReducer() {
  const [state, dispatch] = useReducer(reducer, initialState);
  useEffect(() => {
    axios.get(`https://jsonplaceholder.typicode.com/posts/1`).then((result) => {
      dispatch({ type: "SUCCESS_REQUEST", pyload: result.data })
      .catch(() => {
        dispatch({ type: "ERORR_REQUEST", pyload: {} });
      });
    });
  }, []);
  return (
    <div>
      {state.loading ? "Page Is Loading" : state.post.title}
      {state.error ? state.error : null}
    </div>
  );
}

export default FetchDataUseReducer;
