import { useState } from "react";

function useInputCustomHook() {
  const [value, setValue] = useState("");
  const bind = {
    value,
    onChange: (e) => {
      setValue(e.target.value);
    },
  };
  const reset = () => {
    setValue('')
  }
  return [value, bind , reset];
}

export default useInputCustomHook;
