import React, { useContext } from "react";
import {ContextCounter} from "./MainCompo";

function FCompo() {
  const contextCounter = useContext(ContextCounter);
  return (
    <>
      <p> FCompo Count is : {contextCounter.countValue} </p>
      <button onClick={() => contextCounter.dsipatchCount("increment")}>Increment</button>
      <button onClick={() => contextCounter.dsipatchCount("decrement")}>Decrement</button>
      <button onClick={() => contextCounter.dsipatchCount("reset")}>Reset</button>
    </>
  );
}

export default FCompo;
