import React, { Component } from 'react'

class CompoRefs extends Component {
  constructor(props) {
    super(props)
		this.refInput = React.createRef();
  }
  foucsIt() {
		this.refInput.current.focus()
	}
  render() {
    return (
			<input type='text' ref={this.refInput}/>
    )
  }
}

export default CompoRefs
