import React from 'react'

const CountHOC = WrappedComponent => {
  class newComponent extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        count : 0
      }
    }
    clickHandler = () => {
      this.setState(prevState => {
        return {count : prevState.count + 1}
      })
    }
    render() {
      return (
        <WrappedComponent />
      )
    }
  }
  
  return (
    newComponent
  )
}

export default CountHOC
